Berry.register({
	type: 'barcode',
	defaults: { elType: 'text' },
	update: function(item, silent) {
		if(typeof item === 'object') {
			$.extend(this.item, item);
		}
		$.extend(this, this.item);
		this.setValue(this.value);
		this.render();
		this.setup();
		if(!silent) {
			this.trigger('change');
		}
