chat_init = function({team_id, team_name}={}) {
    this.data.chat = {};
    this.data.chat.team_name = team_name
    // Determine if message is me or someone else
    process_messages = function(messages) {
        for(var i=0;i<messages.length;i++) {
            if (this.data.user.unique_id === messages[i].user.unique_id) {
                messages[i].me = true;
            }
        }
        return messages;
    }

    // Fetch All Messages
    fetch_messages = function(scroll_down=true) {
        this.app.get('msg_list',{team_id:team_id},function(messages){
            this.data.messages=process_messages.call(this,messages);
            this.app.update();
            if (scroll_down) {
                $('.chat-messages').scrollTop($('.chat-messages')[0].scrollHeight);
            }
        })
    }

    // Handle Message Posting
    var myappcontext = this
    $(".chat-textarea").keypress(function (e) {
        if(e.which == 13) {
            var msg_text = $(this).val()
            $(this).val('')
            debugger;
            myappcontext.app.post('msg_submit',{team_id:team_id,message:msg_text,unique_id:myappcontext.data.user.unique_id},function(response){
                fetch_messages.call(myappcontext)
            })
            e.preventDefault();
        }
    });

    // On Init, fetch all messages
    fetch_messages.call(this)
    setInterval(fetch_messages.bind(this), 10000);
}

notes_init = function({team_id, team_name}={}) {
    var localStorageVar = 'epatient_notes_read_'
    this.data.notes = {};
    this.data.team_name = team_name
    this.data.newnotes = 0;

    var fetch_notes = function(callback) {
        this.app.get('notes_list',{team_id:team_id},function(notes){
            var maxId = localStorage.getItem(localStorageVar+team_id);
            notes = _.sortBy(notes, 'updated_at').reverse();
            this.data.newnotes = false
            for(var i=0;i<notes.length;i++) {
                if (notes[i].id > maxId) {
                    notes[i].isnew = true;
                    this.data.newnotes+=1;
                }
            }
            this.data.notes = notes;
            this.app.update();
            if (typeof callback !== 'undefined') {
                callback.call(this);
            }
        })
    }

    $('#notes-modal').on('show.bs.modal', function (event) {
        fetch_notes.call(this,function() {
            var maxId = this.data.notes[0].id;
            localStorage.setItem(localStorageVar+team_id,maxId);
        });
    }.bind(this))

    $('#notes-modal').on('hide.bs.modal', function (event) {
        this.data.newnotes = 0;
        this.app.update();
    }.bind(this))

    fetch_notes.call(this);
    setInterval(fetch_notes.bind(this), 60000);
}

